"use strict";const userInfo=document.getElementById("user-info"),userInfoForm=document.getElementById("user-info-form"),userName=document.getElementById("user-info-name"),lastName=document.getElementById("user-info-surname"),userEmail=document.getElementById("user-info-email"),contentUsers=document.getElementById("content-users"),userInfoButton=document.getElementById("user-info-button"),userInfoTitle=document.getElementById("user-info-title");userInfo.addEventListener("click",e=>{e.target.classList.contains("button--cancel")&&(userInfoButton.textContent="New user",userInfoTitle.textContent="New user",userName.value="",lastName.value="",userEmail.value="",userInfoForm.action="/add-user")}),contentUsers.addEventListener("click",e=>{e.target.classList.contains("button--edit")?(userInfoButton.textContent="Update user",userInfoTitle.textContent="Update user",userName.value=e.target.parentElement.children[0].textContent,lastName.value=e.target.parentElement.children[1].textContent,userEmail.value=e.target.parentElement.children[2].textContent,userInfoForm.action="/update-user/"+e.target.parentElement.dataset.id):e.target.classList.contains("button--delete")&&fetch("/delete-user/"+e.target.parentElement.dataset.id,{method:"DELETE"}).then(e=>e.json()).then(e=>{e.ok&&location.reload()})}),userInfoForm.addEventListener("submit",e=>{e.preventDefault(),""!=userName.value.trim()&&""!=lastName.value.trim()&&""!=userEmail.value.trim()&&e.target.submit()});